import React,{useState} from 'react'

export const AddTodo = (props) => {

const [title,setTitle] = useState("");
const [desc,setDesc] = useState(""); 

const submit = (e)=>{
e.preventDefault();
if(!title || !desc) {
    alert("title or description should not be empty ! !");
}
}

    return (
        <form className='container'>
            <h3 className='my-3'>Add a Todo</h3>
            <form onSubmit={submit}></form>
            <div class="mb-3">
                <label for="title" class="form-label">Todo Title</label>
                <input type="text" value={title} onChange={(e)=>setTitle(e.target.value)} class="form-control" id="title" aria-describedby="titleHelp" />
            </div>
            <div class="mb-3">
                <label for="desc" class="form-label">Description</label>
                <input type="text" value={desc} onChange={(e)=>setDesc(e.target.value)} class="form-control" id="desc" />
            </div>
            <button type="submit" class="btn btn-sm btn-success">Submit</button>
        </form>
    )
}
